mapper.update:
UPDATE CHART

	CHART_CONTENT = #{chartContent}
	NEXT_VISIT_DATE = #{nextVisitDate}
	MEDICINE_NAME = #{medicineName}
	MEDICINE_DOSE = #{medicineDose}

WHERE PATIENT_ID = #{patientId}
AND VISIT_DATE = #{visitDate}
AND EMPLOYEE_NUM = #{employeeNum}

mapper.delete:
DELETE FROM CHART
WHERE PATIENT_ID = #{patientId}
AND VISIT_DATE = #{visitDate}
AND EMPLOYEE_NUM = #{employeeNum}
